import React, {Ref} from 'react';
import NextLink from 'next/link';
import {LinkProps} from 'next/link';
import Router from 'next/router';
import {StyleSheet, Text, TextStyle, View, ViewStyle} from 'react-native';

type Props = {
  href?: string;
  onClick?: () => Promise<void>;
  viewStyle?: ViewStyle;
  textStyle?: TextStyle;
  selected?: boolean;
} & LinkProps &
  TextStyle;
type State = {
  hover: boolean;
};

export default class Link extends React.Component<Props, State> {
  private _textRef: any;

  state: State = {
    hover: false,
  };

  blur() {
    this._textRef.blur();
  }

  focus() {
    this._textRef.focus();
  }

  setNativeProps(props) {
    this._textRef.setNativeProps(props);
  }

  _setRef = (c) => {
    this._textRef = c;
  };

  render() {
    const {
      as,
      passHref, // ignore
      scroll,
      prefetch,
      href,
      shallow,
      replace,
      ...rest
    } = this.props;

    return (
      <NextLink
        href={href}
        passHref={true}
        prefetch={prefetch}
        replace={replace}
        scroll={scroll}
        shallow={shallow}
        onMouseEnter={() => {
          this.setState({
            hover: true,
          });
        }}
        onMouseLeave={() => {
          this.setState({
            hover: false,
          });
        }}>
        <Text accessibilityRole="link" ref={this._setRef} {...rest}>
          {this.props.children}
        </Text>
      </NextLink>
    );
  }
}
