import {TextStyle} from 'react-native';

export const h1: TextStyle = {
  fontSize: 28,
  fontWeight: 'bold',
};

export const h2: TextStyle = {
  fontSize: 24,
  fontWeight: 'bold',
};

export const h3: TextStyle = {
  fontSize: 20,
  fontWeight: 'normal',
};

export const h4: TextStyle = {
  fontSize: 16,
  fontWeight: '200',
};

export const h5: TextStyle = {
  fontSize: 14,
  fontWeight: '200',
