import {TextStyle} from 'react-native';
import {darkText} from 'styles/colors';

export const h1: TextStyle = {
  fontSize: 28,
  fontWeight: 'bold',
  color: darkText,
};

export const h2: TextStyle = {
  fontSize: 24,
  fontWeight: 'bold',
  color: darkText,
};

export const h3: TextStyle = {
  fontSize: 20,
  fontWeight: 'normal',
  color: darkText,
};

export const h4: TextStyle = {
  fontSize: 16,
  fontWeight: '200',
  color: darkText,
};

export const h5: TextStyle = {
  fontSize: 14,
  fontWeight: '200',
  color: darkText,
};
