import React from 'react';
import {NamedStyles, StyleSheet, Text, View} from 'react-native';

type Props = {
  href: string;
  style: NamedStyles;
  selected?: boolean;
};
type State = {
  hover: boolean;
};

export default class Link extends React.Component<Props, State> {
  state: State = {
    hover: false,
  };

  render() {
    const style: NamedStyles = {...styles.root, ...this.props.style};
    return (
      <View
        style={style}
        accessibilityRole="link"
        onMouseEnter={() => {
          console.log('onMouseEnter');
          this.setState({
            hover: true,
          });
        }}
        onMouseLeave={() => {
          this.setState({
            hover: false,
          });
        }}>
        <Text href={this.props.href}>
          {this.props.children} hover={this.state.hover}
        </Text>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  root: {},
});
