import React from 'react';
import {NamedStyles, StyleSheet, Text, View, ViewStyle} from 'react-native';

type Props = {
  href: string;
  viewStyle: NamedStyles;
  selected?: boolean;
};
type State = {
  hover: boolean;
};

export default class Link extends React.Component<Props, State> {
  state: State = {
    hover: false,
  };

  render() {
    const style: ViewStyle = {
      ...styles.root,
      ...this.props.viewStyle,
      opacity: this.state.hover === true ? 0.5 : 1.0,
    };
    console.log('style', style);
    console.log('this.props.viewStyle', this.props.viewStyle);
    return (
      <View
        style={style}
        accessibilityRole="link"
        onMouseEnter={() => {
          console.log('onMouseEnter');
          this.setState({
            hover: true,
          });
        }}
        onMouseLeave={() => {
          console.log('onMouseLeave');
          this.setState({
            hover: false,
          });
        }}>
        <Text href={this.props.href}>
          {this.props.children} hover={this.state.hover}
        </Text>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  root: {},
});
