import React from 'react';
import NextLink from 'next/link';
import {LinkProps} from 'next/link';
import {StyleSheet, Text, TextStyle, View, ViewStyle} from 'react-native';

type Props = {
  href?: string;
  onClick?: () => Promise<void>;
  viewStyle?: ViewStyle;
  textStyle?: TextStyle;
  selected?: boolean;
} & LinkProps &
  TextStyle;
type State = {
  hover: boolean;
};

export default class Link extends React.Component<Props, State> {
  state: State = {
    hover: false,
  };

  render() {
    const styles = StyleSheet.create({
      root: {
        opacity: this.state.hover === true ? 0.5 : 1.0,
      },
    });

    const {
      as,
      passHref, // ignore
      scroll,
      shallow,
      replace,
      ...rest
    } = this.props;

    const style = [styles.root];
    return (
      <View
        style={style}
        onMouseEnter={() => {
          this.setState({
            hover: true,
          });
        }}
        onMouseLeave={() => {
          this.setState({
            hover: false,
          });
        }}>
        <Text
          accessibilityRole="link"
          href={this.props.href}
          onPress={
            this.props.onClick &&
            (async () => {
              this.props.onClick();
            })
          }>
          {this.props.children}
        </Text>
      </View>
    );
  }
}
