import {action, computed, observable, runInAction} from 'mobx';
import client, {USER_KEY} from 'api/client';
import * as cookies from 'utils/cookies';
import {
  GetUsernameDocument,
  GetUsernameQuery,
  GetUsernameQueryVariables,
  LoginDocument,
  LoginMutation,
  LoginMutationVariables,
  SignUpDocument,
  SignUpMutation,
  SignUpMutationVariables,
} from 'api/generated';
import {Store} from 'stores/Store';

export type User = {
  bearerToken: string;
  username?: string;
  email: string;
};

export default class AuthStore extends Store<User> {
  @observable
  user: User | undefined;

  constructor() {
    super();
    const exists = cookies.exists(USER_KEY);
    console.log('exists', exists);
    if (exists) {
      this.user = JSON.parse(cookies.get(USER_KEY)) as User;
      console.log('this.user', this.user);
    }
  }

  hydrate(initialData: User | undefined) {}

  @computed
  get isLoggedIn(): boolean {
    return this.user !== undefined;
  }

  setUser(user: User | undefined) {
    if (user) {
      cookies.set(USER_KEY, JSON.stringify(user, null, 4));
    } else {
      cookies.destroy(USER_KEY);
    }
    runInAction(() => {
      this.user = user;
    });
  }

  async login(
    email: string,
    password: string,
  ): Promise<{
    success: boolean;
    message?: string;
  }> {
    const variables: LoginMutationVariables = {
      email,
      password,
    };
    const result = await client.mutate<LoginMutation>({
      mutation: LoginDocument,
      variables,
    });

    if (result.errors && result.errors.length > 0) {
      // logError('LoginMutation error');
      result.errors.forEach((error) => {
        // logError(error.message);
      });
      return {
        success: false,
        message: result.errors
          .map((error) => {
            return error.message;
          })
          .join('\n'),
      };
    }

    if (!result.data.authenticate?.jwtToken) {
      return {
        success: false,
        message: 'Failed to login',
      };
    }

    const bearerToken = result.data!.authenticate!.jwtToken;

    this.setUser({
      email,
      bearerToken,
    });

    const usernameResult = await client.query<GetUsernameQuery>({
      query: GetUsernameDocument,
      variables: {
        email,
      } as GetUsernameQueryVariables,
    });

    if (usernameResult.errors && usernameResult.errors.length > 0) {
      // logError('GetUsername error');
      usernameResult.errors.forEach((error) => {
        // logError(error.message);
      });

      this.setUser(undefined);
      return {
        success: false,
        message: result.errors
          .map((error) => {
            return error.message;
          })
          .join('\n'),
      };
    }

    const {colour, username} = usernameResult.data!.users!.nodes[0]!;

    this.setUser({
      username,
      email,
      bearerToken,
    });

    return {
      success: true,
    };
  }

  async signUp(
    username: string,
    email: string,
    password: string,
  ): Promise<{success: boolean; message?: string}> {
    const variables: SignUpMutationVariables = {
      username,
      email,
      password,
    };
    const result = await client.mutate<SignUpMutation>({
      mutation: SignUpDocument,
      variables,
    });

    if (result.errors && result.errors.length > 0) {
      return {
        success: false,
        message: result.errors
          .map((error) => {
            return error.message;
          })
          .join('\n'),
      };
    }

    return this.login(email, password);
  }

  @action
  logOut() {
    this.setUser(undefined);
  }
}

const authStore = new AuthStore();

export {authStore};
