import React, {Ref} from 'react';
import NextLink from 'next/link';
import {LinkProps} from 'next/link';
import Router from 'next/router';
import {StyleSheet, Text, TextStyle, View, ViewStyle} from 'react-native';

type Props = {
  href?: string;
  onClick?: () => Promise<void>;
  viewStyle?: ViewStyle;
  textStyle?: TextStyle;
  selected?: boolean;
} & LinkProps &
  TextStyle;
type State = {
  hover: boolean;
};

export default class Link extends React.Component<Props, State> {
  private _textRef: any;

  state: State = {
    hover: false,
  };

  blur() {
    this._textRef.blur();
  }

  focus() {
    this._textRef.focus();
  }

  setNativeProps(props) {
    this._textRef.setNativeProps(props);
  }

  _setRef = (c) => {
    this._textRef = c;
  };

  render() {
    const styles = StyleSheet.create({
      root: {
        opacity: this.state.hover === true ? 0.5 : 1.0,
      },
    });

    const {
      as,
      passHref, // ignore
      scroll,
      shallow,
      replace,
      ...rest
    } = this.props;

    const style = [styles.root];
    return (
      <NextLink
        style={style}
        href={href}
        passHref={true}
        prefetch={prefetch}
        replace={replace}
        scroll={scroll}
        shallow={shallow}
        onMouseEnter={() => {
          this.setState({
            hover: true,
          });
        }}
        onMouseLeave={() => {
          this.setState({
            hover: false,
          });
        }}>
        <Text
          accessibilityRole="link"
          href={this.props.href}
          {...rest}
          onPress={
            this.props.onClick &&
            (async () => {
              this.props.onClick();
            })
          }>
          {this.props.children}
        </Text>
      </NextLink>
    );
  }
}
