import React from 'react';
import {StyleSheet, Text, TextStyle, View, ViewStyle} from 'react-native';

type Props = {
  href: string;
  viewStyle?: ViewStyle;
  textStyle?: TextStyle;
  selected?: boolean;
};
type State = {
  hover: boolean;
};

export default class Link extends React.Component<Props, State> {
  state: State = {
    hover: false,
  };

  render() {
    const styles = StyleSheet.create({
      root: {
        opacity: this.state.hover === true ? 0.5 : 1.0,
      },
    });

    const style = [styles.root, this.props.viewStyle];
    console.log('style', style);
    console.log('this.props.viewStyle', this.props.viewStyle);
    return (
      <View
        style={style}
        onMouseEnter={() => {
          console.log('onMouseEnter');
          this.setState({
            hover: true,
          });
        }}
        onMouseLeave={() => {
          console.log('onMouseLeave');
          this.setState({
            hover: false,
          });
        }}>
        <Text accessibilityRole="link" href={this.props.href}>
          {this.props.children}
        </Text>
      </View>
    );
  }
}
