import {Provider} from 'mobx-react';
import AuthStore from 'stores/AuthStore';

export default async function App({Component, pageProps}) {
  const authStore = await AuthStore.init();
  return (
    <Provider authStore={authStore}>
      <Component {...pageProps} />
    </Provider>
  );
}
